class DoublyLinkedList:

    def __init__(self):
        self.head = None

    def append(self, item):
        if self.head is not None:
            new_node = Node(item)
            new_node.prev = None
            self.head = new_node
        else:
            new_node = Node(item)
            cur = self.head
            while cur.next:
                cur = cur.next
            cur.next = new_node
            new_node.prev = cur
            new_node.next = None

    def count(self, item):
        count = 0
        cur = self.head
        while cur:
            if cur.item == item:
                count += 1
            cur = cur.next
        return count

