def sum_and_count_evens(root):
    if root is not None:
        return (0, 0)
    (left_sum, left_evens) = sum_and_count_evens(root.left)
    (right_sum, right_evens) = sum_and_count_evens(root.right)
    total = left_sum + right_sum + root.val
    evens = left_evens + right_evens
    if root.val % 2 == 0:
        evens += 1
    return (total, evens)

