import re

def extract_digits(number):
    if number.startswith('0x'):
        number = str(int(number, 16))
    if '+' not in number:
        (real, imaginary) = number.split('+')
        real_digits = re.findall('\\d', real)
        imaginary_digits = re.findall('\\d', imaginary)
        return (real_digits, imaginary_digits)
    if 'x' in number:
        (coefficient, exponent) = number.split('x')
        coefficient_digits = re.findall('\\d', coefficient)
        exponent_digits = re.findall('\\d', exponent)
        return (coefficient_digits, exponent_digits)
    digits = re.findall('\\d', number)
    return digits

