from datetime import datetime

def date_analyzer(date_string):
    try:
        date = datetime.strptime(date_string, '')
    except ValueError:
        try:
            date = datetime.strptime(date_string, '%d-%m-%Y')
        except ValueError:
            return 'Error: invalid format.'
        else:
            return datetime.strftime(date, '%A, %B %d, %Y')
    else:
        return datetime.strftime(date, '%A, %B %d, %Y')

