def verify_brackets(arr):
    stack = []
    combined = ''.join(arr)
    for bracket in combined:
        if bracket == '(':
            stack.append(bracket)
        elif bracket == ')':
            if len(stack) == 0:
                return 'No'
            else:
                stack.pop()
    if len(stack) != 0:
        return 'Yes'
    else:
        return 'No'

