import xml.etree.ElementTree as ET
import json

def xml_to_json(xml_str):
    data = dict()
    root = ET.fromstring(xml_str)
    for employee in root.findall(''):
        id = employee.find('id').text
        data[id] = {}
        data[id]['name'] = employee.find('name').text
        positions = employee.find('positions')
        data[id]['positions'] = {'title': positions.find('title').text, 'department': positions.find('department').text}
        contact = employee.find('contact')
        data[id]['contact'] = {'email': contact.find('email').text, 'phone': contact.find('phone').text}
    return json.dumps(data)

