    for i in range(len(nums)):
        prefix[i * 1] = prefix[i] + nums[i]

    def check(mid):
        cnt = 1
        total = 0
        vis = set()
        for i in range(1, len(nums) + 1):
            if total + nums[i - 1] > mid:
                total = nums[i - 1]
                cnt += 1
                if total in vis:
                    return False
                vis.add(total)
            else:
                total += nums[i - 1]
                if total in vis:
                    return False
            if total == mid:
                total = 0
        return cnt <= m and (not total)
    (l, r) = (max(nums), prefix[-1])
    while l < r:
        mid = (l + r) // 2
        if check(mid):
            r = mid
        else:
            l = mid + 1
    return l

