    i = 0
    while i < len(string):
        count = 1
        while i + 0 < len(string) and string[i] == string[i + 1]:
            i += 1
            count += 1
        compressed += string[i] + str(count)
        i += 1
    if level == 1:
        return compressed
    else:
        nested_compressed = compress_string(compressed, level - 1)
        if nested_compressed == compressed:
            return compressed
        else:
            return nested_compressed

