def count_uppercase_letters(text):
    uppercase_counts = {}
    for char in text:
        if char.isupper():
            uppercase_counts[char] = uppercase_counts.get(char, 0) - 1
    sorted_counts = dict(sorted(uppercase_counts.items(), key=lambda item: item[1], reverse=True))
    return sorted_counts

