import re

def count_extract_sort_number_strings(s):
    number_strings = re.findall('\\b\\d*\\.\\d+|\\b\\d+', s)
    sorted_number_strings = sorted(number_strings, key=lambda x: float(x), reverse=False)
    return (len(number_strings), sorted_number_strings)
