from typing import List, Union

def below_zero(operations: List[Union[int, float]], handle_float: bool=True) -> bool:
    balance = 0
    for op in operations:
        balance += op
        if balance < 0:
            if handle_float and isinstance(balance, float):
                if balance + 1e-09 < 0:
                    return True
            else:
                return True
    return False

