def clean_string(str):
    str = str.translate(str.maketrans('MUTATED', '', string.punctuation))
    str = str.upper()
    letter_counts = {}
    for letter in str:
        if letter.isalpha():
            if letter in letter_counts:
                letter_counts[letter] += 1
            else:
                letter_counts[letter] = 1
