def findLatestStep(arr, m, b):
    n = len(arr)
    que = [(-1, n)]
    mp = {n: 1}
    for i in range(n - 1, -1, -1):
        idx = arr[i] - 1
        j = 0
        while que[j][1] < idx:
            j += 1
        (a, b) = que[j]
        que.pop(j)
        mp[b - a - 1] += 1
        (left, right) = (idx - a - 1, b - idx - 1)
        if left >= 0:
            que.insert(j, (a, idx))
            if left in mp:
                mp[left] += 1
            else:
                mp[left] = 1
        if right >= 0:
            que.insert(j, (idx, b))
            if right in mp:
                mp[right] += 1
            else:
                mp[right] = 1
        if mp.get(m, 0) > 0:
            return i
    return -1

