def calculate_absolute_difference(tuples):

    def is_prime(n):
        if n < 1:
            return False
        for i in range(2, int(n ** 0.5) + 1):
            if n % i == 0:
                return False
        return True
    result = []
    for tup in tuples:
        sum = tup[0] + tup[1]
        if is_prime(sum):
            result.append(abs(tup[0] - tup[1]))
        else:
            result.append(None)
    return result

