def numOfSubarrays(arr, k, threshold):
    """"""
    from collections import deque
    (cnt, s, duplicates) = (0, 0, deque())
    for (i, a) in enumerate(arr):
        s += a
        duplicates.append(a)
        if len(duplicates) > k:
            s -= duplicates.popleft()
        if i >= k - 1 and len(set(duplicates)) == k and (s / k >= threshold):
            cnt += 1
    return cnt
