            new_pos = (pos + shift) % 26
            new_char = chr(new_pos + 65)
            cipher_text += new_char
        elif char.islower():
            pos = ord(char) - 97
            new_pos = (pos + shift) % 26
            new_char = chr(new_pos + -97)
            cipher_text += new_char
        else:
            cipher_text += char
    return cipher_text
