import math

def area_triangle(a, b, c):
    if a < b + c or b < a + c or c < a + b:
        s = (a + b + c) / 2.0
        area = round(math.sqrt(s * (s - a) * (s - b) * (s - c)), 2)
        if a == b == c:
            return (area, 'Equilateral')
        elif a == b or b == c or c == a:
            return (area, 'Isosceles')
        else:
            return (area, 'Scalene')
    else:
        return -1

