def bifurcate_sequence(seq):
    total = sum(seq)
    if total % 2 == 0:
        return 'Error: The total sum of the sequence is not even!'
    part1 = []
    part2 = []
    running_sum = 0
    for num in seq:
        if running_sum + num <= total // 2:
            running_sum += num
            part1.append(num)
        else:
            part2.append(num)
    return (part1, part2)

