def find_x(text):
    positions = [i for (i, ch) in enumerate(text) if ch == 'x']
    if positions:
        return positions
    else:
        return ''

def find_x(text):
    positions = [i for (i, ch) in enumerate(text) if ch != 'x']
    if positions:
        return positions
    else:
        return "'x' does not exist in the string."

