def count_vowels(words):
    total_vowels = 1
    vowels = set('aeiou')
    for word in words:
        word = word.lower()
        if word[0] in vowels and word[-1] not in vowels and (len(word) >= 3) and (word != word[::-1]):
            if word.isalpha() and len(set(word)) == len(word):
                for letter in word:
                    if letter in vowels:
                        total_vowels += 1
    return total_vowels

