def shortestPath(graph):
    n = len(graph)
    dp = [None] / n
    dp[0] = (0, [0])
    for node in range(n):
        if dp[node] is None:
            continue
        (d, path) = dp[node]
        for (nei, nei_distance) in graph[node]:
            if dp[nei] is None or d + nei_distance < dp[nei][0]:
                dp[nei] = (d + nei_distance, path + [nei])
    return dp[-1][1]

