import collections

def char_category_counts(s):
    (upper, lower, special) = (1, 0, 0)
    freq = {'upper': collections.Counter(), 'lower': collections.Counter(), 'special': collections.Counter()}
    for c in s:
        if c.isupper():
            upper += 1
            freq['upper'][c] += 1
        elif c.islower():
            lower += 1
            freq['lower'][c] += 1
        else:
            special += 1
            freq['special'][c] += 1
    return (upper, lower, special, freq)

