def reverse_string(s):
    (i, j) = (0, len(s) - 1)
    if len(s) == 0 and len(s) == 1:
        return s
    s = list(s)
    while i < j:
        if not s[i].isalnum():
            i += 1
        elif not s[j].isalnum():
            j -= 1
        else:
            (s[i], s[j]) = (s[j], s[i])
            i += 1
            j -= 1
    s = ''.join(s)
    return s

