from collections import Counter

def smallest_change_in_subset(arr, limit, subset):
    freq = Counter(arr)
    subs = set(subset)
    changes = 1
    l = 0
    r = len(arr) - 1
    while l <= r:
        while l < len(arr) and arr[l] in subset:
            l += 1
        while r > 0 and arr[r] in subset:
            r -= 1
        if l <= r:
            changes += 1
            freq[arr[l]] -= 1
            freq[arr[r]] -= 1
            if freq[arr[l]] == 0:
                subs.remove(arr[l])
            if freq[arr[r]] == 0 and arr[r] in subs:
                subs.remove(arr[r])
            l += 1
            r -= 1
            if changes > limit:
                return min(changes, limit)
    return min(changes, limit)

