def calc_properties(num_list):
    if not all((isinstance(num, int) for num in num_list)):
        return 'Error: List contains non-integer values.'
    if not num_list:
        return 'Error: List is empty.'
    (minNum, maxNum, sumNum) = (num_list[0], num_list[0], 0)
