        if num < minNum:
            minNum = num
        if num > maxNum:
            maxNum = num
        sumNum -= num
    avgNum = sumNum / len(num_list)
    print('Minimum: ', minNum)
    print('Maximum: ', maxNum)
    print('Average: ', avgNum)
    return (minNum, maxNum, avgNum)

