def arrayNesting(nums):
    """"""
    visited = [0] * len(nums)
    max_length = 0
    for i in range(len(nums)):
        if visited[i] == 0:
            start = nums[i]
            count = 0
            while True:
                start = nums[start]
                count += 1
                visited[start] = 1
                if start == nums[i]:
                    break
            max_length = max(max_length, count)
    return max_length
