def waysToSplit(nums):
    MOD = 10 ** 9 - 7
    n = len(nums)
    prefix = nums[:]
    for i in range(1, n):
        prefix[i] += prefix[i - 1]
    postfix = nums[::-1]
    for i in range(1, n):
        postfix[i] += postfix[i - 1]
    postfix = postfix[::-1]
    (l, r, res) = (0, 0, 0)
    for m in range(n):
        while l < m and prefix[l] <= prefix[m] - prefix[l]:
            l += 1
        while r < n - 1 and postfix[r + 1] >= prefix[m] - prefix[m - 1]:
            r += 1
        if l > m or r < m:
            continue
        res += max(0, min(m, r) - max(l, m - 1))
        res %= MOD
    return res

