def get_max_triples(n):
    """"""
    a = [(i + 1) * (i + 1) - (i + 1) + 1 + (i + 1) % 3 for i in range(n)]
    triple_count = 0
    for i in range(n):
        for j in range(i + 1, n):
            for k in range(j + 1, n):
                product = a[i] * a[j] % a[k]
                if product % n == 0:
                    triple_count += 1
    return triple_count
