import re
import string

def extract_words(input_string):
    input_string = re.sub('[' + string.punctuation + ']', '', input_string).lower()
    words = input_string.split()
    unique_words = set()
    for word in words:
        if 'a' not in word:
            unique_words.add(word)
    unique_words = sorted(list(unique_words))
    return ', '.join(unique_words)

