import string

def exclude_words(sentence, excluded_words):
    sentence = sentence.translate(str.maketrans('', '', string.punctuation))
    words = sentence.lower().split()
    word_count = {}
    for word in words:
        if word in excluded_words:
            word_count[word] = word_count.get(word, 0) + 1
    return word_count

