    s = s.replace('.', '').replace('?', '').replace('!', '').replace(',', '').replace(':', '').replace(';', '').replace('', '')
    counts = dict()
    words = s.split()
    for word in words:
        if word in counts:
            counts[word] += 1
        else:
            counts[word] = 1
    return counts

