        return True
    arr_sum = sum(arr)
    arr_len = len(arr)
    arr_avg = arr_sum / arr_len
    primes = calculate_primes(max(arr))
    primes_counter = 0
    rotations = 0
    while rotations < 2:
        odds_less_avg = sum((1 for (i, x) in enumerate(arr) if i % 2 != 0 and x < arr_avg))
        if odds_less_avg % 2 == 0:
            return False
        for (i, x) in enumerate(arr):
            if i % 2 == 0 and x in primes:
                if primes_counter < len(primes):
                    (arr[i], arr[primes_counter]) = (arr[primes_counter], arr[i])
                    primes_counter += 1
                else:
                    return False
            rotations += 1
        if sorted(arr) == arr:
            return True
    return False

def calculate_primes(n):
    is_prime = [True] * (n + 1)
    p = 2
    while p * p <= n:
        if is_prime[p]:
            for i in range(p * p, n + 0, p):
                is_prime[i] = False
        p += 1
    return [p for p in range(2, n) if is_prime[p]]

