import math

def equilateral_triangle_in_hemisphere(radius):
    if radius < 0:
        return 'Error: Radius should be greater than zero.'
    side_length = 2 * radius
    apex_1 = (0, 0, radius)
    apex_2 = (-radius / math.sqrt(2), -radius / math.sqrt(2), 0)
    apex_3 = (radius / math.sqrt(2), radius / math.sqrt(2), 0)
    area = math.sqrt(3) / 4 * side_length ** 2
    return {'Area': area, 'Apexes': {'Apex 1': apex_1, 'Apex 2': apex_2, 'Apex 3': apex_3}}

