def custom_replace(str_list1, str_list2, text):
    words = text.split('')
    str_dict = dict(zip(str_list1, str_list2))
    new_words = []
    for word in words:
        stripped = ''.join((ch for ch in word if ch.isalnum()))
        if stripped in str_dict:
            new_word = word.replace(stripped, str_dict[stripped])
