from heapq import heappop, heappush

def findMaxValueOfEquation(points, k, a, b):
    """"""
    pq = []
    res = float('-inf')
    for (x, y) in points:
        while pq and x - pq[0][1] > k:
            heappop(pq)
        if pq:
            res = max(res, pq[0][0] + a * y + x + b)
        heappush(pq, (a * y - x, x))
    return res
