from typing import List, Tuple

def below_zero(operations: List[Tuple[str, int]], case_insensitive: bool=True) -> bool:
    balance = 0
    for (op_type, value) in operations:
        if case_insensitive:
            op_type = op_type.lower()
        if op_type in ['deposit', 'deposits']:
            balance += value
        elif op_type in ['withdrawal', 'withdrawals']:
            balance -= value
        if balance <= 0:
            return True
    return False

