def segregate_sort(arr, n):
    arr = np.array(arr)
    arr = np.sort(np.unique(arr))[::-1]
    if n >= len(arr):
        return "Error: 'n' is larger than the number of unique elements in the array."
    output = {}
    (div, mod) = divmod(len(arr), n)
    for i in range(n):
        start_index = i * div
