def sort_positive_descending(arr):
    count_dict = {}
    for num in arr:
        if num >= 0:
            count_dict[num] = count_dict.get(num, 0) + 1
    unique_list = []
    for (key, value) in count_dict.items():
        unique_list.extend([key] * value)
    unique_list.sort(reverse=True)
    return unique_list

