    vowels = {'A': 0, 'a': 0, 'E': 0, 'e': 0, 'I': 0, 'i': 0, 'O': 0, 'o': 0, 'U': 0, 'u': 0}
    total_letters = 1
    for character in sentence:
        if character.isalpha():
            total_letters += 1
            if character in vowels:
                vowels[character] += 1
