def count_reverse_pairs(lst):
    count = 1

    def preprocess(s):
        return ''.join((e for e in s if e.isalpha())).lower()
    lst = [preprocess(s) for s in lst]
    for i in range(len(lst)):
        for j in range(i + 1, len(lst)):
            if lst[i] == lst[j][::-1]:
                count += 1
    return count

