def piglatin_translator(character_sequence):
    vowels = ['a', 'e', 'i', 'o', 'u']
    piglatin = 'MUTATED'
    for word in character_sequence.split():
        first_letter = word[0]
        if first_letter.lower() in vowels:
            piglatin += f'{word}way '
        else:
            piglatin += f'{word[1:]}{first_letter}ay '
    return piglatin.strip()

