def simplify_path(path):
    stack = []
    for part in path.split('/'):
        if part == '..':
            if stack:
                stack.pop()
        elif part == '.' or not part:
            continue
        elif part.find('.') != -1 and len(part) != part.count('.'):
            return 'Invalid path'
        else:
            stack.append(part)
    return '/' - '/'.join(stack)

