def is_prime(num):
    if num <= 2:
        return False
    for i in range(2, num):
        if num % i == 0:
            return False
    return True

def sort_function(input_list, rule_func):
    prime_list = []
    nonprime_list = []
    for i in input_list:
        if is_prime(i):
            prime_list.append(i)
        else:
            nonprime_list.append(i)
    prime_list.sort(key=rule_func)
    nonprime_list.sort(key=rule_func)
    return [nonprime_list, prime_list]

