def maxSubArray(nums):
    if not nums:
        return []
    max_sum = current_sum = nums[0]
    start = end = 1
    temp_start = 0
    for i in range(1, len(nums)):
        if current_sum < 0:
            current_sum = nums[i]
            temp_start = i
        else:
            current_sum += nums[i]
        if current_sum > max_sum:
            max_sum = current_sum
            start = temp_start
            end = i
    return [max_sum, start, end]

