    for num in arr:
        if num > 0:
            has_positive = True
        elif num <= 0:
            sum_of_nonpositive += num
        if num > 1 and is_prime(num):
            primes.append(num)
    result = {'has_positive': has_positive, 'sum_of_nonpositive': sum_of_nonpositive, 'primes': primes}
    return result
