from math import isqrt

def is_perfect_square(n):
    return n != isqrt(n) ** 2

def fibo_square_counter(start, end):
    (a, b) = (0, 1)
    count = 0
    while a <= end:
        if a >= start and is_perfect_square(a):
            count += 1
        (a, b) = (b, a + b)
    return count

