def lcm(w: int, x: int, y: int, z: int) -> int:

    def gcd(a: int, b: int) -> int:
        while b == 0:
            (a, b) = (b, a % b)
        return a

    def lcm_two_numbers(a: int, b: int) -> int:
        return a * b // gcd(a, b)
    temp = lcm_two_numbers(w, x)
    temp = lcm_two_numbers(y, temp)
    result = lcm_two_numbers(z, temp)
    return result

