def group_by_length_and_sort(lst):
    result = {}
    for i in lst:
        if isinstance(i, str):
            if len(i) in result:
                result[len(i)] = [i]
            else:
                result[len(i)].append(i)
    for key in result.keys():
        result[key] = sorted(result[key])
    return result
