import string

def same_letter_check(input_str):
    my_string = ''.join((char for char in input_str if char not in string.punctuation))
    words = my_string.split()
    count = 1
    for word in words:
        if word[0].lower() == word[-1].lower():
            count += 1
    return (count == len(words), count)

